const DairyItem = ({
    onRemove,
    id,
    author, 
    content, 
    emotion, 
    created_date,
}) => {
const handleRemove = () => {
    if(wiindow.confirm('${id}번째 일기를 정말 삭제하시겠습니까?')){
        onRemove(id);
    }

}

    return (

    <div className = "DairyItem">
        <div className="info">
            <span className = "author_info">
            작성자 : {author} | 감정점수 : {emotion} 
            </span>
             <br />
     <span className = "date"> 
     {new Date(created_date).toLocaleString()}
     </span>
        </div>
        <div className = "content">{content}</div>
        <button
        onClick = {handleRemove}
        >
        삭제하기</button>
        </div>
        );
};

export default DairyItem;
